import './App.css';
import { useEffect, useState } from 'react';

const defaultPlayer = {
  power: 1,
  toughness: 1
}

function App() {
  const [winner, setWinner] = useState(null);
  const [player1, setPlayer1] = useState(defaultPlayer);
  const [player2, setPlayer2] = useState(defaultPlayer);

  useEffect(()=> {
    if (player1.power > player2.power && player1.toughness > player2.toughness) {
      setWinner("Player1")
    } else if (player2.power > player1.power && player2.toughness > player1.toughness) {
      setWinner("Player2")
    } else {
      setWinner("Draw")
    }
  }, [player1, player2])
  return (
    <div className="App">
      <div>Winner: {winner || "none"}</div>
      <div>
        Player 1
        <form>
          <input id="p1power" type="number"
            onChange={(e)=>setPlayer1((p1) => ({...p1, power: e.target.valueAsNumber}))}
            value={player1.power}>
          </input>
          <label htmlFor="p1power">Player1 Power</label>
          <input id="p1tough" type="number"
            onChange={(e)=>setPlayer1((p1) => ({...p1, toughness: e.target.valueAsNumber}))}
            value={player1.toughness}>
          </input>
          <label htmlFor="p1tough">Player1 Toughness</label>
        </form>
      </div>
      <div>
        Player 2
        <form>
          <input id="p2power" type="number"
            onChange={(e)=>setPlayer2((p2) => ({...p2, power: e.target.valueAsNumber}))}
            value={player2.power}></input>
          <label htmlFor="p2power">Player2 Power</label>
          <input id="p2tough" type="number"
            onChange={(e)=>setPlayer2((p2) => ({...p2, toughness: e.target.valueAsNumber}))}
            value={player2.toughness}>
          </input>
          <label htmlFor="p2tough">Player2 Toughness</label>
        </form>
      </div>
    </div>
  );
}

export default App;
